package com.app.broadwayfashion.view.adapters


import android.view.LayoutInflater
import android.view.ViewGroup
import androidx.recyclerview.widget.RecyclerView
import com.app.broadwayfashion.databinding.GiftCardOccasionItemBinding

import com.app.broadwayfashion.utils.loadImageFromUrl


class GiftCardOccasionAdapter : RecyclerView.Adapter<GiftCardOccasionAdapter.ViewHolder>() {
    // Array of images


    // This method returns our layout
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val binding: GiftCardOccasionItemBinding =
            GiftCardOccasionItemBinding.inflate(LayoutInflater.from(parent.context), parent, false)
        return ViewHolder(binding)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        // This will set the images in imageview
        holder.binding.imgOccasion.loadImageFromUrl(imageList[position])
        holder.binding.tvOccasionName.text = strList[position]


    }

    // This Method returns the size of the Array
    override fun getItemCount(): Int {
        return 4
    }

    var strList = arrayListOf<String>(
        "Birthday",
        "Wedding",
        "Christmas",
        "New Year"
    )
    var imageList =
        arrayListOf<String>(
            "https://image.pngaaa.com/791/90791-middle.png",
            "https://e7.pngegg.com/pngimages/472/672/png-clipart-pink-floral-decor-wedding-invitation-pink-flowers-pink-flower-circle-flower-arranging-wedding-thumbnail.png",
            "data:image/jpeg;base64,",
            "https://cdn1.vectorstock.com/i/1000x1000/08/20/happy-new-year-in-colorful-confetti-circle-frame-vector-18950820.jpg"
        )

    // The ViewHolder class holds the view
    class ViewHolder(val binding: GiftCardOccasionItemBinding) :
        RecyclerView.ViewHolder(binding.root) {


    }
}